/* Minimal placeholder for Chart.js to avoid runtime errors when CDN is blocked.
   This does NOT implement Chart.js API; it's a safe no-op shim for templates that reference Chart.js.
   For full Chart.js functionality, replace with the real Chart.js build in production. */
(function(global){
    try{
        if(!global.Chart){
            global.Chart = function(){
                console.warn('Chart.js is unavailable; using placeholder shim.');
                return { destroy: function(){}, update: function(){}, render: function(){}};
            };
            global.Chart.prototype = {};
        }
    }catch(e){
        // ignore
    }
})(typeof window !== 'undefined' ? window : this);
